mapper.select:
SELECT

FROM promotion_scope


		and mp_id = #{po.mpId}


		and promotion_id = #{po.promotionId}


		and scope_group = #{po.scopeGroup}


		and scope_type = #{po.scopeType}


		and categoryId = #{po.categoryid}


		and brandId = #{po.brandid}


		and handler_status = #{po.handlerStatus}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

