SELECT

FROM promotion


		and theme_id = #{po.themeId}


		and start_time = #{po.startTime}


		and end_time = #{po.endTime}


		and prom_title = #{po.promTitle}


		and prom_platform = #{po.promPlatform}


		and prom_type = #{po.promType}


		and user_scope = #{po.userScope}


		and join_type = #{po.joinType}


		and join_times_limit = #{po.joinTimesLimit}


		and status = #{po.status}


		and platform_id = #{po.platformId}


		and pay_type = #{po.payType}


		and limit_type = #{po.limitType}


		and content_type = #{po.contentType}


		and gift_rule = #{po.giftRule}


		and pay_type_rule = #{po.payTypeRule}


		and gift_limit_4_multy = #{po.giftLimit4Multy}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

