mapper.delete:
delete from exchange_order_record 


		and exchange_id = #{exchangeId}


		and exchange_name = #{exchangeName}


		and old_unit_code = #{oldUnitCode}


		and old_unit_status = #{oldUnitStatus}


		and old_coupon_name = #{oldCouponName}


		and old_batch_code = #{oldBatchCode}


		and old_coupon_type = #{oldCouponType}


		and new_unit_code = #{newUnitCode}


		and new_batch_code = #{newBatchCode}


		and new_coupon_name = #{newCouponName}


		and new_coupon_type = #{newCouponType}


		and user_mail = #{userMail}


		and exchange_time = #{exchangeTime}


		and order_code = #{orderCode}


		and conversion_status= #{conversionStatus}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and platform_id = #{platformId}


		and order_amount = #{orderAmount}


		new_coupon_batch_name = #{newCouponBatchName}


		old_coupon_batch_name = #{oldCouponBatchName}
