mapper.delete:
delete from user_card_record


		and card_name = #{cardName}


		and card_type = #{cardType}


		and settle_method = #{settleMethod}


		and card_state = #{cardState}


		and use_state = #{useState}


		and source_card_id = #{sourceCardId}


		and create_time = #{createTime}


		and update_time = #{updateTime}
