mapper.select:
SELECT

FROM ad_code


		and page_type = #{po.pageType}


		and code = #{po.code}


		and name = #{po.name}


		and parent_code = #{po.parentCode}


		and level = #{po.level}


		and sort = #{po.sort}


		and platform = #{po.platform}


		and width = #{po.width}


		and height = #{po.height}


		and source_size_limit = #{po.sourceSizeLimit}


		and create_by = #{po.createBy}


		and update_by = #{po.updateBy}


		and version = #{po.version}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM ad_code


		and page_type = #{po.pageType}


		and code = #{po.code}


		and name = #{po.name}


		and parent_code = #{po.parentCode}


		and level = #{po.level}


