SELECT

FROM contact_group_sku_stock


		and sku_id = #{po.skuId}


		and contact_group_stock_id = #{po.contactGroupStockId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

