SELECT

FROM contact_group_stock


		and name like '%${po.name}%'


		and spu_id = #{po.spuId}


		and count = #{po.count}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and merchant_id = #{po.merchantId}


		and create_user_name = #{po.createUserName}


		and create_user_id = #{po.createUserId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT

FROM contact_group_stock


		and name = #{po.name}


		and spu_id = #{po.spuId}


		and count = #{po.count}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and merchant_id = #{po.merchantId}


		and create_user_name = #{po.createUserName}


		and create_user_id = #{po.createUserId}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT

FROM contact_group_stock


		and name like concat('%',#{po.name},'%')


		and spu_id = #{po.spuId}


		and count = #{po.count}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and merchant_id = #{po.merchantId}


		and platform_id = #{po.platformId}


		and spu_id
		in

			#{spuId}



		and  merchant_id
		in

			#{merchantId}



		and create_user_name = #{po.createUserName}


		and create_user_id = #{po.createUserId}





		ORDER BY ${page.orderBy}


		ORDER BY id desc



	limit #{page.limitStart},#{page.pageSize}

