SELECT cgs.id,cgs.name FROM contact_group_stock cgs LEFT JOIN contact_group_sku_stock cgss on cgs.id = cgss.contact_group_stock_id LEFT JOIN contact_group_pu_stock cgps on cgps.contact_group_sku_id = cgss.id
and cgs.merchant_id = #{po.merchantId}
and cgps.su_id = #{suId}
