mapper.select:
SELECT

FROM merchant_product_activity_stock


		and activity_id = #{po.activityId}


		and activity_name = #{po.activityName}


		and activity_type = #{po.activityType}


		and threshold_num = #{po.thresholdNum}


		and already_sale_num = #{po.alreadySaleNum}


		and parent_id = #{po.parentId}


		and single_product_num = #{po.singleProductNum}


		and warehouse_id = #{po.warehouseId}


		and stock_limit = #{po.stockLimit}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

