mapper.select:
select real_stock_num-real_frozen_stock_num
from merchant_product_virtual_stock
where sku_id=#{skuId}

SELECT

FROM merchant_product_virtual_stock


		and product_id = #{po.productId}


		and merchant_id = #{po.merchantId}


		and is_support_virtualstock = #{po.isSupportVirtualstock}


		and is_support_unlimitedstock = #{po.isSupportUnlimitedstock}


		and real_stock_num = #{po.realStockNum}


		and real_frozen_stock_num = #{po.realFrozenStockNum}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM merchant_product_virtual_stock


		and product_id = #{po.productId}


		and merchant_id = #{po.merchantId}


		and is_support_virtualstock = #{po.isSupportVirtualstock}


		and is_support_unlimitedstock = #{po.isSupportUnlimitedstock}


		and real_stock_num = #{po.realStockNum}


		and real_frozen_stock_num = #{po.realFrozenStockNum}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}

