FROM store_stock_change_apply_picture


		and store_stock_change_apply_id = #{po.storeStockChangeApplyId}


		and picture_path = #{po.picturePath}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




