mapper.select:
SELECT

FROM store_stock_change_apply


		and store_product_unit_id = #{po.storeProductUnitId}


		and stock_change = #{po.stockChange}


		and before_change_value = #{po.beforeChangeValue}


		and after_change_value = #{po.afterChangeValue}


		and apply_cause_id = #{po.applyCauseId}


		and concreteness_cause = #{po.concretenessCause}


		and after_user_id = #{po.afterUserId}


		and after_user_name = #{po.afterUserName}


		and after_store_id = #{po.afterStoreId}


		and after_store_name like concat('%',#{po.afterStoreName},'%')


		and after_time = #{po.afterTime}


		and operation_user_id = #{po.operationUserId}


		and operation_user_name = #{po.operationUserName}


		and operation_time = #{po.operationTime}


		and is_consent = #{po.isConsent}


		and platform_id = #{po.platformId}


		and commodity_product_unit_name like concat('%',#{po.commodityProductUnitName},'%')


		and product_unit_serial_number like concat('%',#{po.productUnitSerialNumber},'%')


		and type = #{po.type}


		and after_time > #{po.afterTimeStart}


		and #{po.afterTimeStop} > after_time




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

