SELECT

FROM wms_warehouse


		and short_name = #{po.shortName}


		and warehouse_name = #{po.warehouseName}


		and warehouse_desc = #{po.warehouseDesc}


		and warehouse_type = #{po.warehouseType}


		and warehousestatus = #{po.warehousestatus}


		and is_real_warehouse = #{po.isRealWarehouse}


		and storage_type = #{po.storageType}


		and function_type = #{po.functionType}


		and is_support_tran = #{po.isSupportTran}


		and is_support_sby = #{po.isSupportSby}


		and flex = #{po.flex}


		and stock_type = #{po.stockType}


		and is_oem = #{po.isOem}


		and business_hours = #{po.businessHours}


		and cut_off_time = #{po.cutOffTime}


		and country_id = #{po.countryId}


		and province_id = #{po.provinceId}


		and city_id = #{po.cityId}


		and county_id = #{po.countyId}


		and address_name = #{po.addressName}


		and warehouse_longitude = #{po.warehouseLongitude}


		and warehouse_latitude = #{po.warehouseLatitude}


		and warehouse_group_email = #{po.warehouseGroupEmail}


		and warehouse_phone = #{po.warehousePhone}


		and warehouse_fax = #{po.warehouseFax}


		and warehouse_contactor = #{po.warehouseContactor}


		and warehouse_contactor_mobile = #{po.warehouseContactorMobile}


		and rtv_default_express = #{po.rtvDefaultExpress}


		and rtv_sp_receive_address = #{po.rtvSpReceiveAddress}


		and express_remark = #{po.expressRemark}


		and return_duty_person = #{po.returnDutyPerson}


		and return_phone = #{po.returnPhone}


		and return_mobile = #{po.returnMobile}


		and do_return_duty_person = #{po.doReturnDutyPerson}


		and do_return_phone = #{po.doReturnPhone}


		and do_return_mobile = #{po.doReturnMobile}


		and customer_return_address = #{po.customerReturnAddress}


		and warehouse_reamrk = #{po.warehouseReamrk}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

