mapper.update:
update merchant_product_virtual_stock
set real_frozen_stock_num=real_frozen_stock_num+#{count}
where sku_id=#{skuId}
and real_frozen_stock_num+#{count} <= real_stock_num

update merchant_product_virtual_stock
set real_frozen_stock_num=real_frozen_stock_num-#{count},real_stock_num = real_stock_num-#{count}
where sku_id=#{skuId}

update merchant_product_virtual_stock
set real_stock_num = real_stock_num-#{count}
where sku_id=#{skuId}

mapper.delete:
delete from merchant_product_virtual_stock 


		standard_product_unit_id = #{standardProductUnitId},


		and merchant_id = #{merchantId}


		and is_support_virtualstock = #{isSupportVirtualstock}


		and is_support_unlimitedstock = #{isSupportUnlimitedstock}


		and real_stock_num = #{realStockNum}


		and real_frozen_stock_num = #{realFrozenStockNum}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and platform_id = #{platformId}


		and sku_id = #{skuId}

mapper.update:
update merchant_product_virtual_stock
set real_stock_num=real_stock_num-#{realStockNum},real_frozen_stock_num=real_frozen_stock_num-#{realStockNum}
where sku_id=#{skuId} and real_stock_num-#{realStockNum} > 0
