mapper.update:
UPDATE merchant_product_warehouse_stock SET real_frozen_stock_num = real_frozen_stock_num -#{realFrozenStockNum},
real_stock_num = real_stock_num -#{realFrozenStockNum}
WHERE sku_id = #{skuId}

mapper.delete:
delete from merchant_product_warehouse_stock 


		and sku_id = #{skuId}


		and product_id = #{productId}


		and merchant_id = #{merchantId}


		and warehouse_id = #{warehouseId}


		and merchant_product_id = #{merchantProductId}


		and real_stock_num = #{realStockNum}


		and real_frozen_stock_num = #{realFrozenStockNum}


		and pm_warehouse_priority = #{pmWarehousePriority}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and platform_id = #{platformId}
