mapper.delete:
delete from channel_service_config


		and enterprise_id = #{enterpriseId}


		and channel_code = #{channelCode}


		and channel_service_name = #{channelServiceName}


		and channel_service_type = #{channelServiceType}


		and channel_service_method = #{channelServiceMethod}


		and channel_service_url = #{channelServiceUrl}


		and if_encrypted = #{ifEncrypted}


		and if_decrypt = #{ifDecrypt}


		and encrypt_type = #{encryptType}


		and state = #{state}


		and create_time = #{createTime}


		and update_time = #{updateTime}
