SELECT ca.id, ca.name, ca.short_code, c.type, c.name channel_name, c.short_code channel_short_code, c.path FROM channel_activity ca,channel c
ca.channel_id = c.id
and ca.name like concat('%',#{po.name},'%')
and c.name like concat('%', #{po.channelName},'%')
and ca.platform_id = #{po.platformId}
ORDER BY ${page.orderBy}
ORDER BY id desc
limit #{page.limitStart},#{page.pageSize}
