mapper.select:
SELECT

FROM u_client_pay_type_config


		and client_id = #{po.clientId}


		and pay_type_code = #{po.payTypeCode}


		and index_code = #{po.indexCode}


		and is_stop = #{po.isStop}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}





		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_client_pay_type_config


		and client_id = #{po.clientId}


		and pay_type_code = #{po.payTypeCode}


		and index_code = #{po.indexCode}


		and is_stop = #{po.isStop}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}

SELECT

FROM u_client_pay_type_config


		and client_id = #{po.clientId}


		and pay_type_code = #{po.payTypeCode}


		and index_code = #{po.indexCode}


		and is_stop = #{po.isStop}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}



ORDER BY index_code DESC
