mapper.select:
SELECT

FROM u_code_module


		and parent_id = #{po.parentId}


		and code_module_name = #{po.codeModuleName}


		and sort_value = #{po.sortValue}


		and description = #{po.description}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_code_module


		and parent_id = #{po.parentId}


		and code_module_name = #{po.codeModuleName}


		and sort_value = #{po.sortValue}


		and description = #{po.description}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}

SELECT

FROM u_code_module


		and parent_id = #{po.parentId}


		and code_module_name = #{po.codeModuleName}


		and sort_value = #{po.sortValue}


		and description = #{po.description}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id
