mapper.select:
SELECT

FROM download


		and name = #{po.name}


		and session = #{po.session}


		and url = #{po.url}


		and daily_downloads = #{po.dailyDownloads}


		and downloads = #{po.downloads}


		and create_time = #{po.createTime}


		and uodate_time = #{po.uodateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT

FROM download


		and name like concat('%',#{po.name},'%') 


		and session like concat('%',#{po.session},'%')


		and url = #{po.url}


		and daily_downloads = #{po.dailyDownloads}


		and downloads = #{po.downloads}


		and create_time = #{po.createTime}


		and uodate_time = #{po.uodateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM download


