SELECT

FROM insurance_login


		and user_id = #{po.userId}


		and login_name = #{po.loginName}


		and password = #{po.password}


		and login_times = #{po.loginTimes}


		and last_login = #{po.lastLogin}


		and cardno = #{po.cardno}


		and nickname = #{po.nickname}


		and shopname = #{po.shopname}


		and headerurl = #{po.headerurl}


		and dccuid = #{po.dccuid}


		and memberid = #{po.memberid}


		and openid = #{po.openid}


		and userno = #{po.userno}


		and mid = #{po.mid}


		and vs_id = #{po.vsId}


		and membercode = #{po.membercode}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

