mapper.select:
SELECT

FROM u_menu


		and id = #{po.id}


		and parent_id = #{po.parentId}


		and name = #{po.name}


		and sort = #{po.sort}


		and sys_code = #{po.sysCode}


		and href = #{po.href}


		and icon = #{po.icon}


		and is_show = #{po.isShow}


		and permission = #{po.permission}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY sort	



limit #{page.limitStart},#{page.pageSize}

