mapper.select:
SELECT

FROM u_role


		and id = #{po.id}


		and name = #{po.name}


		and enname = #{po.enname}


		and role_type = #{po.roleType}


		and is_sys = #{po.isSys}


		and sys_code = #{po.sysCode},


		and useable = #{po.useable}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_role


		and id = #{po.id}


		and name = #{po.name}


		and enname = #{po.enname}


		and role_type = #{po.roleType}


		and is_sys = #{po.isSys}


		and sys_code = #{po.sysCode},


		and useable = #{po.useable}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}

SELECT

FROM u_role


		and id = #{po.id}


		and name like concat('%',#{po.name},'%')


		and enname = #{po.enname}


		and role_type = #{po.roleType}


		and is_sys = #{po.isSys}


		and sys_code = #{po.sysCode}


		and useable = #{po.useable}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT

FROM u_role
LEFT JOIN u_user_role ur ON ur.role_id = #{po.id}


		and useable = #{po.useable}

SELECT r.id, r.name, r.enname, r.role_type, r.is_sys, r.sys_code, r.useable, r.remarks, r.platform_id, r.create_time, r.update_time, r.update_user_id, r.update_user_name FROM u_role r
and useable = #{po.useable}
and r.enname = #{po.enname}
and r.name = #{po.name}
and r.create_time <= #{maxCreateDate}
and r.create_time >= #{minCreateDate}

SELECT
count(1) cnt
FROM u_role r



		and useable = #{po.useable}


		and r.enname = #{po.enname}


		and r.name = #{po.name}


		and r.create_time  <=   #{maxCreateDate}


		and r.create_time  >=   #{minCreateDate}
