mapper.select:
SELECT

FROM u_user_info


		and user_id = #{po.userId}


		and info_id = #{po.infoId}


		and is_read = #{po.isRead}


		and is_deleted = #{po.isDeleted}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY create_time desc



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_user_info


		and user_id = #{po.userId}


		and info_id = #{po.infoId}


		and is_read = #{po.isRead}


		and is_deleted = #{po.isDeleted}


		and platform_id = #{po.platformId}

SELECT

FROM u_user_info


		and user_id = #{po.userId}


		and info_id = #{po.infoId}


		and is_read = #{po.isRead}


		and is_deleted = #{po.isDeleted}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT 
ui.id 
from u_user_info ui, u_info i,u_info_send_way isw  
WHERE ui.info_id = i.id 
and i.id = isw.info_template_id 
and ui.is_read = 0 
and isw.send_way_id = #{type}
and ui.user_id = #{userId} 
and i.platform_id = #{platformId}
