SELECT count(1) cnt FROM u_user u,u_user_extend ue, u_user_platform up
u.id = ue.id and u.id = up.user_id and ue.account_status = 0
and u.mobile = #{mobile}
and u.company_id = 2
and u.company_id != 2
and up.platform_id = #{platformId}

SELECT u.id, u.login_name, u.password, u.mobile, u.mail, u.qrcode, u.salt, u.update_salt, u.company_id, u.is_available, u.enterprise_id, u.type, ue.name, ue.regtime, ue.account_status, ue.channel_source, u.channel_user_unique FROM u_user u,u_user_extend ue,u_user_platform up WHERE u.id = ue.id and up.user_id = u.id and account_status = 0 and ue.status = 1 and u.channel_user_unique = #{channelUserUnique} and up.platform_id = #{platformId}
and u.company_id = #{companyId}
ORDER BY ue.regtime desc

SELECT u.id, u.login_name, u.password, u.mobile, u.mail, u.qrcode, u.salt, u.update_salt, u.company_id, u.enterprise_id, u.type, u.is_available, ue.name, ue.regtime, ue.account_status, u.channel_user_unique FROM u_user u,u_user_extend ue,u_user_platform up,u_user_login ul WHERE u.id = ue.id and up.user_id = u.id and u.id = ul.user_id and account_status = 0 and ue.status = 1 and u.channel_user_unique = #{channelUserUnique} and up.platform_id = #{platformId}
and u.company_id = #{companyId}
ORDER BY ul.login_time desc,ue.regtime desc limit 0,1
