mapper.update:
UPDATE u_user_extend SET is_deleted = 1


		and sex = #{sex}


		and nickname = #{nickname}


		and head_pic_url = #{headPicUrl}


		and name = #{name}


		and regtime = #{regtime}


		and birthday = #{birthday}


		and mobile = #{mobile}


		and qq = #{qq}


		and member_code = #{memberCode}


		and remark = #{remark}


		and lastlogin = #{lastlogin}


		and activity_info = #{activityInfo}


		and is_complete = #{isComplete}


		and device_id = #{deviceId}


		and device_type = #{deviceType}


		and weixin = #{weixin}


		and workaddress_id = #{workaddressId}


		and status = #{status}


		and account_status = #{accountStatus}


		and invalid_time = #{invalidTime}


		and address = #{address}


		and is_administrator = #{isAdministrator}


		and is_deleted = #{isDeleted}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and channel_source = #{channelSource},

update u_user_extend ue,u_user u
set ue.invalid_time = null,u.is_available=1,u.quit_time=null
where ue.id = u.id and ue.id = #{id}
