mapper.delete:
delete from versions 


		and versions_mark = #{versionsMark}


		and type = #{type}


		and release_date = #{releaseDate}


		and is_constraint = #{isConstraint}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and url=#{url}


		and description=#{description}


		and version_code=#{versionCode}


		and install_name = #{installName},


		and resume = #{resume},


		and is_official = #{isOfficial},


		and version_status = #{versionStatus},


		and platform_id = #{platformId}
